import{_ as i}from"./chunks/index.31PWG4W-.js";import{_ as e,c as r,o,j as t,G as d,aA as h,a as n}from"./chunks/framework.B5199-QK.js";const m=JSON.parse('{"title":"八大绩效域","description":"","frontmatter":{},"headers":[],"relativePath":"book/performance.md","filePath":"book/performance.md","lastUpdated":1755224186000}'),s={name:"book/performance.md"};function c(u,a,b,p,q,f){const l=i;return o(),r("div",null,[a[0]||(a[0]=t("h1",{id:"八大绩效域",tabindex:"-1"},[n("八大绩效域 "),t("a",{class:"header-anchor",href:"#八大绩效域","aria-label":'Permalink to "八大绩效域"'},"​")],-1)),d(l,{readTime:"26",words:"9.2k"}),a[1]||(a[1]=h('<p>价值驱动的项目管理知识体系关注价值的实现，包含了项目管理原则、绩效域、项目生命周期、过程组、 10大知识领域和价值交付系统。 在整个生命周期过程中，项目管理者需要始终坚持项目管理原则，通过涵盖10大知识领域的项目管理过程组对项目进行管理，同时需要密切关注<strong>干系人、团队、开发方法和生命周期、规划、项目工作、交付、测量、不确定性</strong>这8个于绩效密切相关的因素，我们称之为绩效域。</p><div class="info custom-block"><p class="custom-block-title">🚨</p><p>系统内的每个绩效域相互作用、相互关联和相互依赖，并协调一致、共同运作，支撑项目目标和价值的实现。</p><p>这些绩效域相互重叠和关联，并在项目中同时开展，我们不能把某一个绩效域当作一个孤立的工作进行处理。在实际项目进行过程中， 每个绩效域中开展的活动是由组织的背景、项目、可交付物、项目团队、干系人和其他因素确定的，它们之间并没有特定的执行顺序和权重。</p></div><details class="details custom-block"><summary>本章目录</summary><nav class="table-of-contents"><ul><li><a href="#一、干系人绩效域">一、干系人绩效域</a><ul><li><a href="#绩效要点">绩效要点</a></li><li><a href="#与其他绩效域的相互作用">与其他绩效域的相互作用</a></li><li><a href="#预期目标和执行效果检查">预期目标和执行效果检查</a></li></ul></li><li><a href="#二、团队绩效域">二、团队绩效域</a><ul><li><a href="#绩效要点-1">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-1">与其他绩效域的相互作用</a></li><li><a href="#预期目标和执行效果检查-1">预期目标和执行效果检查</a></li></ul></li><li><a href="#三、开发方法和生命周期绩效域">三、开发方法和生命周期绩效域</a><ul><li><a href="#绩效要点-2">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-2">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查">执行效果检查</a></li></ul></li><li><a href="#四、规划绩效域">四、规划绩效域</a><ul><li><a href="#绩效要点-3">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-3">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查-1">执行效果检查</a></li></ul></li><li><a href="#五、项目工作绩效域">五、项目工作绩效域</a><ul><li><a href="#绩效要点-4">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-4">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查-2">执行效果检查</a></li></ul></li><li><a href="#六、交付绩效域">六、交付绩效域</a><ul><li><a href="#绩效要点-5">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-5">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查-3">执行效果检查</a></li></ul></li><li><a href="#七、度量绩效域">七、度量绩效域</a><ul><li><a href="#绩效要点-6">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-6">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查-4">执行效果检查</a></li></ul></li><li><a href="#八、不确定性绩效域">八、不确定性绩效域</a><ul><li><a href="#绩效要点-7">绩效要点</a></li><li><a href="#与其他绩效域的相互作用-7">与其他绩效域的相互作用</a></li><li><a href="#执行效果检查-5">执行效果检查</a></li></ul></li></ul></nav></details><h2 id="一、干系人绩效域" tabindex="-1">一、干系人绩效域 <a class="header-anchor" href="#一、干系人绩效域" aria-label="Permalink to &quot;一、干系人绩效域&quot;">​</a></h2><p>干系人绩效域涉及与干系人相关的活动和职能。在项目整个生命周期中，有效执行本绩效域可以实现的目标主要包括：</p><ol><li>与干系人建立高效的工作关系</li><li>干系人认同项目目标</li><li>支持项目的干系人提高了满意度，并从中收益</li><li>反对项目的干系人没有对项目产生负面影响</li></ol><p>在项目整个生命周期过程中，<em><strong>为了有效执行干系人绩效域，项目经理需要重点促进干系人的参与。</strong></em></p><h3 id="绩效要点" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-促进干系人参与" tabindex="-1">1. 促进干系人参与 <a class="header-anchor" href="#_1-促进干系人参与" aria-label="Permalink to &quot;1. 促进干系人参与&quot;">​</a></h4><p>项目经理需要在整个项目生命周期过程中持续促进干系人参与到项目中，因此，在项目开始时，就需要和干系人一起定义并共享清晰的项目愿景，并就项目愿景和干系人达成共识。</p><p>为了有效地让干系人参与，项目经理可带领项目团队按照以下步骤开展工作：</p><ol><li><p>识别</p></li><li><p>理解和分析</p></li><li><p>优先级排序</p></li><li><p>参与</p></li><li><p>监督</p></li></ol><p>可以通过干系人满意度指标来评估干系人绩效域的有效性。 通常可以通过与干系人的对话来确定干系人满意度，也可以通过项目或迭代审查会、产品审查会和其他方法获得定期反馈。 如果有大量的干系人，还可以使用问卷调查来评估满意度，必要时，甚至可以通过更新干系人参与方法来提高干系人的满意度。</p><h3 id="与其他绩效域的相互作用" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>很多项目工作都是围绕着促进干系人参与、与干系人进行沟通而展开的，干系人参与到项目的很多方面，某些干系人可以帮助减少项目的不确定性，而有些干系人则可能导致不确定的增加。</p><p>干系人主要参与的项目工作包括：</p><ol><li>为项目团队定义需求和范围，并对其进行优先级排序</li><li>参与并制定规划</li><li>确定项目可交付物和项目成果的验收和质量标准</li><li>客户、高层管理人员、项目管理办公室领导或项目集经理等干系人将重点关注项目及其可交付物绩效的测量</li></ol><h3 id="预期目标和执行效果检查" tabindex="-1">预期目标和执行效果检查 <a class="header-anchor" href="#预期目标和执行效果检查" aria-label="Permalink to &quot;预期目标和执行效果检查&quot;">​</a></h3><p>在整个项目声明周期过程中，项目经理和项目团队需要对干系人绩效域的执行效果进行检查，确保其有效执行并实现预期目标。</p><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><strong>建立高效的工作关系</strong></td><td><code>干系人参与的连续性</code>：通过观察，记录方式对干系人参与的连续性进行衡量</td></tr><tr><td><strong>干系人认同项目目标</strong></td><td><code>变更的频率</code>：对项目范围、产品需求的大量变更或修改可能表明干系人没有参与进来或与项目目标不一致</td></tr><tr><td><strong>支持项目的干系人提高了满意度，并从中收益；反对项目的干系人没有对项目产生负面影响</strong></td><td><code>干系人行为</code>：干系人的行为可表明项目受益人是否对项目感到满意和表示支持，或者他们是否反对项目 <br><code>干系人满意度</code>：可通过调研、访谈和焦点小组方式，确定干系人满意度，判断干系人是否感到满意和表示支待，或者他们对项目及其可交付物是否表示反对<br><code>干系人相关问题和风险</code>：对项目问题日志和风险登记册的审查可以识别与单个干系人有关的问题和风险</td></tr></tbody></table><h2 id="二、团队绩效域" tabindex="-1">二、团队绩效域 <a class="header-anchor" href="#二、团队绩效域" aria-label="Permalink to &quot;二、团队绩效域&quot;">​</a></h2><p>团队绩效域涉及项目团队人员有关的活动和职能。在项目整个生命周期过程中，有效执行本绩效域可以实现预期目标，主要包括：</p><ol><li>共享责任</li><li>建立高绩效团队</li><li>所有团队成员都展现出相应的领导力和人际关系技能</li></ol><h3 id="绩效要点-1" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-1" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><p>为了有效执行团队绩效域，项目经理需要重点关注：</p><h4 id="_1-项目团队文化" tabindex="-1">1.项目团队文化 <a class="header-anchor" href="#_1-项目团队文化" aria-label="Permalink to &quot;1.项目团队文化&quot;">​</a></h4><p>项目经理可以通过如下方法，确保形成和维护一个安全、尊重、无偏见的团队文化，支待团队成员坦诚沟通：透明、诚信、尊重、积极的讨论、支持、勇气、庆祝成功</p><h4 id="_2-高绩效项目团队" tabindex="-1">2.高绩效项目团队 <a class="header-anchor" href="#_2-高绩效项目团队" aria-label="Permalink to &quot;2.高绩效项目团队&quot;">​</a></h4><p>项目经理和项目团队通过以下方式打造高绩效项目团队：开诚布公的沟通、共识、共享责任、信任、协作、适应性、韧性、赋能、认可</p><h4 id="_3-领导力技能" tabindex="-1">3.领导力技能 <a class="header-anchor" href="#_3-领导力技能" aria-label="Permalink to &quot;3.领导力技能&quot;">​</a></h4><p>领导力相关的特征和活动包括：建立和维护愿景、批判性思维、激励、人际关系技能、决策、冲突管理</p><h3 id="与其他绩效域的相互作用-1" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-1" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>团队绩效域聚焦于项目经理和项目团队成员在整个项目生命周期过程中的技能，这些技能 已融入项目的其他各个方面，在整个项目期间项目团队成员都需要全程展现团队相关的领导力 素质和技能。</p><h3 id="预期目标和执行效果检查-1" tabindex="-1">预期目标和执行效果检查 <a class="header-anchor" href="#预期目标和执行效果检查-1" aria-label="Permalink to &quot;预期目标和执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><code>共享责任</code></td><td><strong>目标和责任心</strong>：所有项目团队成员都了解愿景和目标。项目团队对项目的可交付物和项目成果承担责任</td></tr><tr><td><code>建立高绩效团队</code></td><td><strong>信任与协作程度</strong>：项目团队彼此信任，相互协作<br><strong>适应变化的能力</strong>：项目团队适应不断变化的情况，并在面对挑战时有韧性<br><strong>彼此赋能</strong>：项目团队感到被赋能，同时项目团队对其成员赋能并认可</td></tr><tr><td><code>所有团队成员都展现出相应的领导力和人际关系技能</code></td><td><strong>管理和领导力风格适宜性</strong>：项目团队成员运用批判性思维和人际关系技能；项目团队成员的管理和领导力风格适合项目的背景和环境</td></tr></tbody></table><h2 id="三、开发方法和生命周期绩效域" tabindex="-1">三、开发方法和生命周期绩效域 <a class="header-anchor" href="#三、开发方法和生命周期绩效域" aria-label="Permalink to &quot;三、开发方法和生命周期绩效域&quot;">​</a></h2><p>有效执行本绩效域可以实现预期目标，主要包含:</p><ul><li>开发方法与项目可交付物相符合</li><li>将项目交付与干系人价值紧密关联</li><li>项目生命周期由促进交付节奏的项目阶段和产生项目交付物所需的开发方法组成</li></ul><p>在项目整个生命周期过程中，为了有效执行开发方法和生命周期绩效域，项目经理需要重点关注：交付节奏、开发方法及其选择、协调交付节奏和开发方法及生命周期。</p><h3 id="绩效要点-2" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-2" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-交付节奏" tabindex="-1">1. 交付节奏 <a class="header-anchor" href="#_1-交付节奏" aria-label="Permalink to &quot;1. 交付节奏&quot;">​</a></h4><p>交付节奏是指项目可交付物的时间安排和频率，项目可以一次性交付、多次交付、定期交付和持续交付。</p><ul><li>一次性交付：只在项目结束时交付。</li><li>多次交付：个项目可能包含多个组件，这些组件会在整个项目期间的不同时间交付，因此有些项目会进行多次交付。</li><li>定期交付：定期交付与多次交付非常相似，但定期交付是按固定的交付计划进行，例如每月或每两个月交付一次。 新的软件应用程序可能每两周进行一次内部交付，然后定期向市场发布。</li><li>持续交付：持续交付是将项目特性增量交付给客户，通常通过使用小批量工作和自动化技术完成。 待续交付可用于数字化产品，从产品管理的角度看，持续交付聚焦千在整个产品。生命周期内产生的收益和价值。 持续交付中可能存在许多开发周期和维护活动，这种交付类型更适合于人员稳定的项目团队。</li></ul><h4 id="_2-开发方法" tabindex="-1">2. 开发方法 <a class="header-anchor" href="#_2-开发方法" aria-label="Permalink to &quot;2. 开发方法&quot;">​</a></h4><p>开发方式是在项目生命周期内创建产品、服务或结果的方法。当前行业普遍认同的三种开发方法是预测型方法、混合型方法、适应型方法。</p><ul><li><code>预测型方法</code>：又称为瀑布型方法。这种开发方法相对稳定，范围、进度、成本、资源和风险可以在项目生命周期的早期阶段进行明确定义。<strong>在项目开始时可以定义、收集和分析项目和产品的需求，此时适合于采用预测型方法。</strong></li><li><code>混合型方法</code>：混合型开发方法是适应型方法和预测型方法的结合体。<strong>当需求存在不确定性或风险时，这种开发方法非常有用</strong>。 混合型方法通常使用迭代型方法或增量型方法。</li><li><code>适应型方法</code>：适应型方法在项目开始时确立了明确的愿景，之后在项目进行过程中在最初已知需求基础上，按照用户反馈、环境或意外事件来不断完善、说明、更改或替换。<strong>当需求面临高度的不确定性和易变性，且在整个项目期间不断变化时，适合采用适应型方法。</strong> <strong>敏捷方法可以视为一种适应性方法。</strong></li></ul><h4 id="_3-开发方法的选择" tabindex="-1">3. 开发方法的选择 <a class="header-anchor" href="#_3-开发方法的选择" aria-label="Permalink to &quot;3. 开发方法的选择&quot;">​</a></h4><p>产品、服务或成果，项目和组织都会影响开发方法的选择。影响开发方法的产品、服务或成果的相关因素包括：</p><p>产品、服务或成果：</p><ul><li>创新程度</li><li>需求确定性</li><li>变更的难易程度</li><li>交付物的性质</li><li>风险</li><li>安全需求</li><li>法规</li></ul><p>项目：</p><ul><li>干系人</li><li>进度制约因素</li><li>资金可用情况</li></ul><p>组织：</p><ul><li>组织结构</li><li>文化</li><li>组织能力</li><li>项目团队的规模和所处位置</li></ul><h4 id="_4-协调交付节奏和开发方法" tabindex="-1">4. 协调交付节奏和开发方法 <a class="header-anchor" href="#_4-协调交付节奏和开发方法" aria-label="Permalink to &quot;4. 协调交付节奏和开发方法&quot;">​</a></h4><h3 id="与其他绩效域的相互作用-2" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-2" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>开发方法和生命周期绩效域与干系人绩效域、规划绩效域、不确定性绩效域、交付绩效域、 项目工作绩效域和团队绩效域相互作用。</p><h3 id="执行效果检查" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><code>开发方法与项目可交付物相符合</code></td><td><strong>产品质量和变更成本</strong>：采用适宜的开发方法(预测型、混合型或适应型)，可交付物的产品变量比较高，变更成本相对较小</td></tr><tr><td><code>将项目交付与干系人价值紧密联系</code></td><td><strong>价值导向型项目阶段</strong>：按照价值导向将项目工作从启动到收尾划分为多个项目阶段，项目阶段中包括适当的退出标准</td></tr><tr><td><code>项目生命周期由促进交付节奏的项目阶段和产生项目交付物所需的开发方法组成</code></td><td><strong>适宜的交付节奏和开发方法</strong>：如果项目具有多个可交付物，且交付节奏和开发方法不同，可将生命周期阶段进行重叠或重复</td></tr></tbody></table><h2 id="四、规划绩效域" tabindex="-1">四、规划绩效域 <a class="header-anchor" href="#四、规划绩效域" aria-label="Permalink to &quot;四、规划绩效域&quot;">​</a></h2><p>规划绩效域涉及整个项目期间组织与协调相关的活动与职能，这些活动和职能是最终交付项目和成果所必须的。</p><p>有效执行本绩效域可以实现预期目标，主要包含:</p><ul><li>项目以有条理、协调一致的方式推进</li><li>应用系统的方法交付项目成果</li><li>对演变情况进行详细说明</li><li>规划投入的时间成本是适当的</li><li>规划的内容对管理干系人的需求而言是充分的</li><li>可以根据新出现的和不断变化的需求进行调整</li></ul><p>为了有效执行规划绩效域，项目经理需要重点关注：规划的影响因素、项目估算、项目团队组成和结构规划、沟通规划、实物资源规划、采购规划、变更规划、度量指标和一致性。</p><h3 id="绩效要点-3" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-3" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-规划的影响因素" tabindex="-1">1. 规划的影响因素 <a class="header-anchor" href="#_1-规划的影响因素" aria-label="Permalink to &quot;1. 规划的影响因素&quot;">​</a></h4><ul><li>开发方法 <ul><li>采用预测型开发方法，在项目生命周期早期进行规划或组织，这种情况下，大部分规划都是预先进行的。</li><li>预先进行高层级规划，随后使用原型方法进行设计，在项目团队和干系入对设计表示同意后，项目团队再完成更详细的规划。</li><li>项目团队实施迭代的适应型方法，一些规划会提前进行，以便制订发布计划，而进一步的规划会在每个迭代开始时进行。</li></ul></li><li>项目可交付物</li><li>组织需求</li><li>市场条件</li><li>法律或法规限制</li></ul><h4 id="_2-项目估算" tabindex="-1">2. 项目估算 <a class="header-anchor" href="#_2-项目估算" aria-label="Permalink to &quot;2. 项目估算&quot;">​</a></h4><p>影响估算的4个方面的因素包括：区间、准确度、精确度、信心</p><h4 id="_3-项目团队组成和结构规划" tabindex="-1">3. 项目团队组成和结构规划 <a class="header-anchor" href="#_3-项目团队组成和结构规划" aria-label="Permalink to &quot;3. 项目团队组成和结构规划&quot;">​</a></h4><p>规划项目团队的组成和结构时，首先要确定完成项目工作所需的技能组合，包括技能、熟练程度和类似项目的经验。</p><h4 id="_4-沟通规划" tabindex="-1">4. 沟通规划 <a class="header-anchor" href="#_4-沟通规划" aria-label="Permalink to &quot;4. 沟通规划&quot;">​</a></h4><p>沟通是争取干系人有效参与的最重要的因素，对沟通进行规划时，需要与干系人绩效域进行关联，包括干系入识别、分析、优先级排序和参与的内容。</p><h4 id="_5-实物资源规划" tabindex="-1">5. 实物资源规划 <a class="header-anchor" href="#_5-实物资源规划" aria-label="Permalink to &quot;5. 实物资源规划&quot;">​</a></h4><p>实物资源指人力资源以外的任何资源，包括材料、设备、软件、测试环境、许可证等。规划实物资源需要对资源的交付、移动、存储和处置进行规划，包括对实物资源的流动轨迹进行跟踪。需要大量实物材料的项目，会从战略角度思考和规划实物资源从订单到交付再到使用全 过程的时间安排，同时需要考虑批量订购的存储成本、全球物流、可待续性，将实物资产与项目的其余部分进行整合管理。</p><h4 id="_6-采购规划" tabindex="-1">6. 采购规划 <a class="header-anchor" href="#_6-采购规划" aria-label="Permalink to &quot;6. 采购规划&quot;">​</a></h4><p>采购可以在项目期间的任何时候进行，预先规划有助千明确目标，确保采购过程顺利进行。</p><h4 id="_7-变更规划" tabindex="-1">7. 变更规划 <a class="header-anchor" href="#_7-变更规划" aria-label="Permalink to &quot;7. 变更规划&quot;">​</a></h4><p>项目期间会发生很多变更，有些变更是因发生风险事件或项目环境变化而导致的，有些则 是基于对需求的深入了解之后提出的，还有些变更则是由千客户或其他原因造成的。</p><h4 id="_8-度量指标和一致性" tabindex="-1">8. 度量指标和一致性 <a class="header-anchor" href="#_8-度量指标和一致性" aria-label="Permalink to &quot;8. 度量指标和一致性&quot;">​</a></h4><ul><li>度量指标。项目进行过程中，规划、交付和度量工作之间存在自然的联系，这种联系就是度量指标。确定度量指标、基准和临界值，以及确定测试和评估方法及流程是规划绩效域的重要工作。度量目标的原则是<strong>只测量重要的东西</strong>。</li><li>一致性。要保证规划和实际的一致性。</li></ul><h3 id="与其他绩效域的相互作用-3" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-3" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>项目团队和干系人将制定度量指标，并将绩效与计划进行比较</p><h3 id="执行效果检查-1" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查-1" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><code>项目以有条理、协调一致的方式推进</code></td><td><strong>绩效偏差</strong>：对照项目基准和其他度量指标对项目结果进行绩效审查表明项目正在按计划进行，绩效偏差处于临界值范围内</td></tr><tr><td><code>应用系统的方法交付项目成果</code></td><td><strong>规划的整体性</strong>：</td></tr><tr><td><code>对演变情况进行详细说明</code></td><td><strong>规划的整体性</strong>：</td></tr><tr><td><code>规划投入的时间成本是适当的</code></td><td><strong>规划的充分性</strong>：</td></tr><tr><td><code>可以根据新出现的和不断变化的需求进行调整</code></td><td><strong>可适应变化</strong>：</td></tr></tbody></table><h2 id="五、项目工作绩效域" tabindex="-1">五、项目工作绩效域 <a class="header-anchor" href="#五、项目工作绩效域" aria-label="Permalink to &quot;五、项目工作绩效域&quot;">​</a></h2><p>项目工作绩效域涉及项目工作相关的活动和职能。项目工作可使项目团队保持专注，并使项目活动顺利进行。 在项目整个生命周期过程中，有效执行本绩效域可以实现预期目标，主要包含：</p><ul><li>高效且有效的项目绩效</li><li>适合项目和环境的项目过程</li><li>干系人适当的沟通和参与</li><li>对实物资源进行了有效管理</li><li>对采购进行了有效管理</li><li>有效处理了变更</li><li>通过持续学习和过程改进提高了团队能力</li></ul><p>为了有效执行本绩效域，项目经理需要重点关注：</p><ul><li>项目过程</li><li>项目制约因素</li><li>专注于工作过程和能力</li><li>管理沟通和参与</li><li>管理实物资源</li><li>处理采购事宜</li><li>监督新工作和变更</li><li>学习与持续改进</li></ul><h3 id="绩效要点-4" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-4" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-项目过程" tabindex="-1">1. 项目过程 <a class="header-anchor" href="#_1-项目过程" aria-label="Permalink to &quot;1. 项目过程&quot;">​</a></h4><p>程评估可以包括过程审计和质量保证活动。可按照项目需要，使用如下方法来优化过程:</p><ul><li>精益生产法：通过价值流图来测量增值活动和非增值活动之间的比率，用以识别是否存在非增值的冗余活动。</li><li>召开回顾会议：召开回顾会议或经验教训相关的会议可以使项目团队审查自己的工作方式，并在必要时提出改进建议，以改善流程和效率。</li><li>价值导向审查：以价值为导向，审视“下一笔资金应该花在哪里”，帮助项目团队确定他们应该继续执行当前任务还是进入下一项活动，以便优化价值交付。</li></ul><h4 id="_2-项目制约因素" tabindex="-1">2. 项目制约因素 <a class="header-anchor" href="#_2-项目制约因素" aria-label="Permalink to &quot;2. 项目制约因素&quot;">​</a></h4><p>制约因素包括最后交付日期、法律法规、固定预算和质量政策等，在整个项目生命周期过程中，制约因素可能会发生变化。</p><h4 id="_3-专注于工作过程和能力" tabindex="-1">3. 专注于工作过程和能力 <a class="header-anchor" href="#_3-专注于工作过程和能力" aria-label="Permalink to &quot;3. 专注于工作过程和能力&quot;">​</a></h4><p>为了使项目交付和干系入价值最大化，项目工作要聚焦在工作过程(交付价值)和保护项目团队的工作能力(项目团队的高效性和满意度)两个方面，从而使项目团队专注于交付价值，并始终了解项目的进展情况，包括何时发生潜在问题、进度是否延迟和成本是否超支等。</p><h4 id="_4-管理沟通和参与" tabindex="-1">4. 管理沟通和参与 <a class="header-anchor" href="#_4-管理沟通和参与" aria-label="Permalink to &quot;4. 管理沟通和参与&quot;">​</a></h4><p>在整个项目生命周期过程中，大部分项目工作都需要与干系人进行沟通，此时需要按照第14章<strong>项目沟通管理</strong>过程执行，并<strong>关联干系人绩效域</strong>。</p><p>如有大量的新的沟通请求提出，说明沟通规划不足以满足干系人的需要，此时需要干系人进一步参与，对沟通计划进行变更。</p><h4 id="_5-管理实物资源" tabindex="-1">5. 管理实物资源 <a class="header-anchor" href="#_5-管理实物资源" aria-label="Permalink to &quot;5. 管理实物资源&quot;">​</a></h4><p><strong>管理实物资源的目标主要包括</strong>：</p><ul><li>减少或消除现场的材料搬运和储存</li><li>消除材料等待时间</li><li>最小化报废和浪费</li><li>促进安全的工作环境等</li></ul><h4 id="_6-处理采购事宜" tabindex="-1">6. 处理采购事宜 <a class="header-anchor" href="#_6-处理采购事宜" aria-label="Permalink to &quot;6. 处理采购事宜&quot;">​</a></h4><p>在大多数组织中，项目经理没有签订合同的权限，他们会与合同签约负责人或在合同、法律和法规方面具有专业知识的其他入员共同开展工作。</p><p>一旦选定供应商，就需要对项目管理计划和相关文件进行更新，并将该供应商纳入项目干系入统一管理。采购在项目生命周期过程中的任何阶段都可进行。</p><h4 id="_7-监督新工作和变更" tabindex="-1">7. 监督新工作和变更 <a class="header-anchor" href="#_7-监督新工作和变更" aria-label="Permalink to &quot;7. 监督新工作和变更&quot;">​</a></h4><p>敏捷或适应型项目中，项目工作会不断演变和调整。</p><h4 id="_8-学习与持续改进" tabindex="-1">8. 学习与持续改进 <a class="header-anchor" href="#_8-学习与持续改进" aria-label="Permalink to &quot;8. 学习与持续改进&quot;">​</a></h4><p>项目团队需要定期召开会议，确定未来在哪些方面可以做得更好(经验教训)，以及如何在下一次迭代或下一阶段工作中对过程做出改进(回顾)，在不断学习中优化工作方式，持续改进过程，支持项目完成最优成果。</p><p><strong>项目具有临时性特点，项目完成后大部分知识可能会丢失，因此项目完成后的知识转移对组织非常重要。</strong></p><h3 id="与其他绩效域的相互作用-4" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-4" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>项目工作绩效域与项目的其他绩效域相互作用，而且对其他绩效域具有促进作用:</p><ol><li>项目工作可促进并支持有效率且有效果的规划、交付和度量</li><li>项目工作可为项目团队互动和干系人参与提供有效的环境</li><li>项目工作可为驾驭不确定性、模糊性和复杂性提供支待，平衡其他项目制约因素。</li></ol><h3 id="执行效果检查-2" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查-2" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><strong>高效且有效的项目绩效</strong></td><td><code>状态报告</code>：通过状态报告可以表明项目工作有效率且有效果</td></tr><tr><td><strong>适合项目和环境的项目过程</strong></td><td>- <code>过程的适宜性</code>：证据表明，项目过程是为满足项目和环境的需要而裁剪的<br>- <code>过程相关性和有效性</code>：过程审计和质量保证活动表明，过程具有相关性且正得到有效使用</td></tr><tr><td><strong>干系人适当的沟通和参与</strong></td><td><code>沟通有效性</code>：项目沟通管理计划和沟通文件表明，所计划的信息与干系人进行了沟通，如有新的信息沟通需求或误解，可能表明干系人的沟通和参与活动缺乏成效</td></tr><tr><td><strong>对实物资源进行了有效管理</strong></td><td><code>资源利用率</code>：所用材料的数量、抛弃的废料和返工量表明，资源正得到高效利用</td></tr><tr><td><strong>对采购进行了有效管理</strong></td><td><code>采购过程适宜</code>：采购审计表明，所采用的适当流程足以开展采购工作，而且承包商正在按计划开展工作</td></tr><tr><td><strong>有效处理了变更</strong></td><td><code>变更处理情况</code>：使用预测型方法的项目已建立变更日志，该日志表明，正在对变更做出全面评估，同时考虑了范围、进度、预算、资源、干系人和风险的影响；采用适应型方法的项目已建立待办事项列表，该列表显示完成范围的比率和增加新范围的比率</td></tr><tr><td><strong>通过持续学习和过程改进提高了团队能力</strong></td><td><code>团队绩效</code>：团队状态报告表明，错误和返工减少，而效率提高</td></tr></tbody></table><h2 id="六、交付绩效域" tabindex="-1">六、交付绩效域 <a class="header-anchor" href="#六、交付绩效域" aria-label="Permalink to &quot;六、交付绩效域&quot;">​</a></h2><p>有效执行本绩效域可以实现预期目标，主要包含:</p><ol><li>项目有助于实现业务目标和战略</li><li>项目实现了预期成果</li><li>在预定时间内实现了项目收益</li><li>项目团队对需求有清晰的理解</li><li>干系人接受项目可交付物和成果，并对其满意。</li></ol><p>项目经理需要重点关注<strong>价值的交付、可交付物、质量</strong>。</p><h3 id="绩效要点-5" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-5" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-价值的交付" tabindex="-1">1. 价值的交付 <a class="header-anchor" href="#_1-价值的交付" aria-label="Permalink to &quot;1. 价值的交付&quot;">​</a></h4><p>在项目进展过程中 就可以向干系人交付价值;而在项目生命周期结束时才发布可交付物的项目，则会在项目完成后产生价值。有的项目在项目结束后的一段时间内，还可以继续获得价值。</p><h4 id="_2-可交付物" tabindex="-1">2. 可交付物 <a class="header-anchor" href="#_2-可交付物" aria-label="Permalink to &quot;2. 可交付物&quot;">​</a></h4><p>可交付物反映了干系人的需求、范围和质量。</p><ul><li>需求启发。需求启发是指引导流出、产生或唤起干系人的需求，包括记录相关需求并征得干系人同意。</li><li>不断演变和发现的需求。可以使用原型、演示、故事板和模型等方法，通过需求的演变，让干系人“眼见为实”地制定需求。</li><li>管理需求。求管理入员使用专用软件、待办事项列表、索引卡、需求跟踪矩阵等方法，确保需求相对稳定，并确保新的和不断变化的需求获得干系人的认同。</li><li>定义范围和管理变更。</li></ul><p>在稳定的环境中运行的项目通常会面临“范围蔓延”，为了应对范围蔓延，项目团队会使用变更控制系统来处理范围变更。</p><h4 id="_3-质量" tabindex="-1">3. 质量 <a class="header-anchor" href="#_3-质量" aria-label="Permalink to &quot;3. 质量&quot;">​</a></h4><p>交付不仅仅是范围和需求。范围和需求聚焦千需要交付的内容，而质量聚焦千需要达到的绩效水平。与质量相关的成本由项目所在的组织承担。</p><h3 id="与其他绩效域的相互作用-5" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-5" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>交付绩效域是在规划绩效域中所执行所有工作的终点。交付节奏基千开发方法和生命周期绩效域中工作的结构方式。 项目工作绩效域通过建立各种过程、管理实物资源、管理采购等促使交付工作。</p><h3 id="执行效果检查-3" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查-3" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><strong>项目有助于实现业务目标和战略</strong></td><td><code>目标一致性</code>：组织的战略计划、可行性研究报告以及项目授权文件表明，项目可交付物和业务目标保持一致</td></tr><tr><td><strong>项目实现了预期成果</strong></td><td><code>项目完成度</code>：项目基础数据表明，项目仍处于正轨，可实现预期成果</td></tr><tr><td><strong>在预定时间内实现了项目收益</strong></td><td><code>项目收益</code>：进度表明财务指标和所规划的交付正在按计划实现</td></tr><tr><td><strong>项目团队对需求有清晰地理解</strong></td><td><code>需求稳定性</code>：在预测型项目中，初始需求的变更很少，表明对需求的真正理解度较高。在需求不断演变的适应型项目中，项目进展中阶段性需求确认反应了干系人对需求的理解</td></tr><tr><td><strong>干系人接受项目可交付物和成果，并对其满意</strong></td><td><code>干系人满意度</code>：访谈、观察和最终用户反馈可表明干系人对可交付物的满意度。<br> <code>质量问题</code>：投诉或退货等质量相关问题的数量也可用于表示满意度</td></tr></tbody></table><h2 id="七、度量绩效域" tabindex="-1">七、度量绩效域 <a class="header-anchor" href="#七、度量绩效域" aria-label="Permalink to &quot;七、度量绩效域&quot;">​</a></h2><p>度量绩效域涉及评估项目绩效和采取应对措施相关的活动和职能。度量是评估项目绩效， 并采取适当的应对措施，以保待最佳项目绩效的过程。</p><p>项目整个生命周期过程中，有效执行本绩效域可以实现预期目标，主要包含：</p><ul><li>对项目状况充分理解；</li><li>据充分，可支持决策；</li><li>及时采取行动，确保项目最佳绩效；</li><li>能够基于预测和评估作出决策，实现目标并产生价值。</li></ul><p>在项目整个生命周期过程中，为了有效执行度量绩效域，项目经理需要重点关注：<strong>制定有效的度量指标、度量内容及相应指标、展示度最信息和结果、度量陷阱、基于度量进行诊断、持续改进</strong></p><h3 id="绩效要点-6" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-6" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-制定有效的度量指标" tabindex="-1">1. 制定有效的度量指标 <a class="header-anchor" href="#_1-制定有效的度量指标" aria-label="Permalink to &quot;1. 制定有效的度量指标&quot;">​</a></h4><p>有效的度量指标可以跟踪、评估和报告项目进展，有助千描述项目状态、改善项目绩效，并降低绩效恶化的可能性，使项目团队能够利用度量结果及时做出决策并采取有效行动。 制定有效的度量指标有助于确保对正确的事情进行度量并向干系人报告。</p><ol><li>关键绩效指标（KPI）：KPI有提前指标和滞后指标。提前指标可以量化，滞后指标比提前指标更容易测量</li><li>有效度量指标：项目团队应该只测量相关的必要内容，并确保度量指标有用。有效的度量指标具有<code>SMART特征</code>。 <ul><li>S=Specific（具体的）</li><li>M=Measurable（有意义的）</li><li>A=Attainable（可实现的）</li><li>R=Relevant（具有相关性）</li><li>T=Time-bount（具有及时性）</li></ul></li></ol><h4 id="_2-度量内容及相应指标" tabindex="-1">2. 度量内容及相应指标 <a class="header-anchor" href="#_2-度量内容及相应指标" aria-label="Permalink to &quot;2. 度量内容及相应指标&quot;">​</a></h4><p>常见的度量指标类别包括：</p><ul><li>可交付物的度量指标</li><li>交付的度量指标</li><li>基准绩效的度量指标</li><li>资源的度量指标</li><li>价值的度量指标</li><li>干系人的度量指标</li><li>预测型度量指标</li></ul><h4 id="_3-展示度量信息和结果" tabindex="-1">3. 展示度量信息和结果 <a class="header-anchor" href="#_3-展示度量信息和结果" aria-label="Permalink to &quot;3. 展示度量信息和结果&quot;">​</a></h4><p>度量信息要想使其在项目中有用，就必须可以及时、容易地获取，易于吸收和领会，并加以展示，以图表的方式可视化地展示度輩信息或结果，可以帮助干系人理解</p><ul><li>仪表盘</li><li>大型可见图表</li><li>任务板</li><li>燃烧图（燃起图和燃尽图）</li></ul><h4 id="_4-度量陷阱" tabindex="-1">4. 度量陷阱 <a class="header-anchor" href="#_4-度量陷阱" aria-label="Permalink to &quot;4. 度量陷阱&quot;">​</a></h4><p>常见的度量陷阱：</p><ul><li>霍桑效应：霍桑效应指出，对某一事物进行度量时会对其行为产生影响，因此需要谨慎制定度量指标。例如，仅度量项目团队可交付物的输出，会鼓励项目团队专注于创建更多数量的可交付物，而不是仅仅专注于度量客户更满意的那些可交付物。</li><li>虚荣指标：对决策没有帮助的度量指标一般属于虚荣指标。例如，新访问者的数量比网站的页面访问量更加有用。</li><li>士气低落：如果设定了无法实现的度量指标和目标，项目团队的士气可能会下降，因此需要设定拓展性目标和激励人心的度量指标。人们都渴望辛勤工作得到认可，不现实或无法实现的目标会适得其反。</li><li>误用度量指标：尽量避免度量指标的误用。</li><li>确认偏见：度量过程中应尽量摆脱偏见</li><li>相关性与因果关系混淆。</li></ul><h4 id="_5-基于度量进行诊断" tabindex="-1">5. 基于度量进行诊断 <a class="header-anchor" href="#_5-基于度量进行诊断" aria-label="Permalink to &quot;5. 基于度量进行诊断&quot;">​</a></h4><p>可以针对各种度晕指标（如进度、预算、速度和项目特有的其他度量指标）制定临界值，偏差程度将取决千干系入的风险承受力。</p><h4 id="_6-持续改进" tabindex="-1">6. 持续改进 <a class="header-anchor" href="#_6-持续改进" aria-label="Permalink to &quot;6. 持续改进&quot;">​</a></h4><p>度量、展示度量信息和度量结果的目的是为了持续改进，为了优化项目绩效和效率。度量结果和相关报告有助于：</p><ul><li>避免问题或缺陷</li><li>防止绩效下降</li><li>促使项目团队学习，提高能力</li><li>改进产品或项目绩效</li><li>推动决策</li><li>更好地创造价值等。</li></ul><h3 id="与其他绩效域的相互作用-6" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-6" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>度量绩效域与规划绩效域、项目工作绩效域和交付绩效域相互作用。</p><h3 id="执行效果检查-4" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查-4" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><strong>对项目状况充分理解</strong></td><td><code>度量结果和报告</code>：通过审计度量结果和报告，可表明数据是否可靠</td></tr><tr><td><strong>数据充分，可支持决策</strong></td><td><code>度量结果</code>：度量结果可表明项目是否按预期进行，或者是否存在偏差</td></tr><tr><td><strong>及时采取行动，确保项目最佳绩效</strong></td><td><code>度量结果</code>：度量结果提供了提前指标以及当前状态，可导致及时的决策和行动</td></tr><tr><td><strong>能够基于预测和评估作出决策，实现目标并产生价值</strong></td><td><code>工作绩效数据</code>：回顾过去的预测和当前工作绩效数据可发现，以前的预测是否准确地反映了目前的情况。将实际绩效与计划绩效进行比较，并评估业务文档，可表明项目实现预期价值的可能性</td></tr></tbody></table><h2 id="八、不确定性绩效域" tabindex="-1">八、不确定性绩效域 <a class="header-anchor" href="#八、不确定性绩效域" aria-label="Permalink to &quot;八、不确定性绩效域&quot;">​</a></h2><p>广义的不确定性是一种不可知或不可预测的状态，不确定包括以下意义：</p><ul><li>风险：与不可知未来事件相关的风险</li><li>模糊性：与不了解当前或未来状态相关的模糊性</li><li>复杂性：与具有不可预测结果的动态系统相关的复杂性</li></ul><p>成功驾驭不确定性首先要了解项目运行的环境。造成项目不确定性的环境因素主要包括：</p><ol><li>经济因素，例如价格波动、资源可用性、借款能力以及通货膨胀/通货紧缩</li><li>技术因素，例如新技术、与系统相关的复杂性以及接口</li><li>法律的或者立法的约束或要求</li><li>与安全、天气和工作条件相关的物理环境</li><li>与当前或未来条件相关的模糊性</li><li>由舆论和媒体塑造的社会和市场影响</li><li>组织外部或内部的政策和职权结构影响等</li></ol><p>有效执行本绩效域可以实现预期目标，主要包含：</p><ol><li>了解项目的运行环境，包括技术、社会、政治、 市场和经济环境等；</li><li>积极识别、分析和应对不确定性；</li><li>了解项目中多个因素之间的相互依赖关系；</li><li>能够对威胁和机会进行预测，了解问题的后果；</li><li>最小化不确定性对项目交付的负面影响；</li><li>能够利用机会改进项目的绩效和成果；</li><li>有效利用成本和进度储备，与项目目标保持一致等。</li></ol><h3 id="绩效要点-7" tabindex="-1">绩效要点 <a class="header-anchor" href="#绩效要点-7" aria-label="Permalink to &quot;绩效要点&quot;">​</a></h3><h4 id="_1-风险" tabindex="-1">1. 风险 <a class="header-anchor" href="#_1-风险" aria-label="Permalink to &quot;1. 风险&quot;">​</a></h4><p>风险是不确定性的一个方面，消极风险称为威胁，积极风险称为机会。<strong>项目是不确定性程度各异的独特性工作，因此所有项目都有风险。</strong></p><h4 id="_2-模糊性" tabindex="-1">2. 模糊性 <a class="header-anchor" href="#_2-模糊性" aria-label="Permalink to &quot;2. 模糊性&quot;">​</a></h4><h4 id="_3-复杂性" tabindex="-1">3. 复杂性 <a class="header-anchor" href="#_3-复杂性" aria-label="Permalink to &quot;3. 复杂性&quot;">​</a></h4><h4 id="_4-不确定性的应对方法" tabindex="-1">4. 不确定性的应对方法 <a class="header-anchor" href="#_4-不确定性的应对方法" aria-label="Permalink to &quot;4. 不确定性的应对方法&quot;">​</a></h4><h3 id="与其他绩效域的相互作用-7" tabindex="-1">与其他绩效域的相互作用 <a class="header-anchor" href="#与其他绩效域的相互作用-7" aria-label="Permalink to &quot;与其他绩效域的相互作用&quot;">​</a></h3><p>从产品或可交付物角度看，不确定性绩效域与其他7个绩效域都相互作用：</p><h3 id="执行效果检查-5" tabindex="-1">执行效果检查 <a class="header-anchor" href="#执行效果检查-5" aria-label="Permalink to &quot;执行效果检查&quot;">​</a></h3><table tabindex="0"><thead><tr><th>预期目标</th><th>指标及检查方法</th></tr></thead><tbody><tr><td><strong>了解项目的运行环境，包括技术、社会、政治、市场和经济环境等</strong></td><td><code>环境因素</code>：团队在评估不确定性、风险和应对措施时考虑了环境因素</td></tr><tr><td><strong>积极识别、分析和应对不确定性</strong></td><td><code>风险应对措施</code>：与项目制约因素（例如，预算、进度和绩效）的优先级排序保持一致</td></tr><tr><td><strong>了解项目中多个因素之间的相互依赖关系</strong></td><td><code>应对措施适宜性</code>：应对风险、复杂性和模糊性的措施适合于项目</td></tr><tr><td><strong>能够对威胁和机会进行预测，了解问题的后果</strong></td><td><code>风险管理机制或系统</code>： 用于识别、分析和应对风险的系统非常强大</td></tr><tr><td><strong>最小化不确定性对项目交付的负面影响</strong></td><td><code>项目绩效处于临界值内</code>：满足计划的交付日期，预算执行情况处于偏差临界值内</td></tr><tr><td><strong>能够利用机会改进项目的绩效和成果</strong></td><td><code>利用机会的机制</code>：团队使用既定机制来识别和利用机会</td></tr><tr><td><strong>有效利用成本和进度储备，与项目目标保持一致</strong></td><td><code>储备使用</code>：团队采取步骤主动预防威胁，有效使用成本或进度储备</td></tr></tbody></table>',178))])}const x=e(s,[["render",c]]);export{m as __pageData,x as default};
