import{_ as a,c as l,o as i,aA as c}from"./chunks/framework.B5199-QK.js";const s=JSON.parse('{"title":"计算题相关概念及公式","description":"","frontmatter":{},"headers":[],"relativePath":"summary/calc-concept.md","filePath":"summary/calc-concept.md"}'),o={name:"summary/calc-concept.md"};function t(r,e,d,C,n,V){return i(),l("div",null,e[0]||(e[0]=[c('<h1 id="计算题相关概念及公式" tabindex="-1">计算题相关概念及公式 <a class="header-anchor" href="#计算题相关概念及公式" aria-label="Permalink to &quot;计算题相关概念及公式&quot;">​</a></h1><h2 id="成本进度计算题相关概念和公式" tabindex="-1">成本进度计算题相关概念和公式 <a class="header-anchor" href="#成本进度计算题相关概念和公式" aria-label="Permalink to &quot;成本进度计算题相关概念和公式&quot;">​</a></h2><h3 id="spi、cpi、ev、ac、pv、etc、eac、vac等概念" tabindex="-1">SPI、CPI、EV、AC、PV、ETC、EAC、VAC等概念 <a class="header-anchor" href="#spi、cpi、ev、ac、pv、etc、eac、vac等概念" aria-label="Permalink to &quot;SPI、CPI、EV、AC、PV、ETC、EAC、VAC等概念&quot;">​</a></h3><ul><li><code>EV（Earned Value）</code>：即挣值，也被称作已完成工作预算成本</li><li><code>AC（Actual Cost）</code>：是实际成本，即实际完成工作所花费的成本</li><li><code>PV（Planned Value）</code>：代表计划价值，又称计划工作预算成本</li><li><code>BAC（Budget At Completion）</code>：项目完工预算，指完成项目所有工作的总预算</li><li><code>CV（Cost Variance）</code>：成本偏差，通过公式 CV = EV - AC 计算。当 CV &gt; 0 ，表示 EV 大于 AC，成本节约</li><li><code>SV（Schedule Variance）</code>：进度偏差，根据公式 SV = EV - PV 计算。SV &gt; 0 时，EV 大于 PV，项目进度提前</li><li><code>SPI（Schedule Performance Index）</code>：进度绩效指数，是项目管理中用于衡量项目进度效率的关键指标。SPI&lt;1：实际工作量小于计划工作量（滞后），大于1（进度超前） SPI = EV / PV 例：SPI为80.00%时表示进度为正常进度的80%（滞后）</li><li><code>CPI（Cost Performance Index）</code>：成本绩效指数，是项目管理中用于衡量成本效率的关键指标。CPI＞1：挣值大于实际成本，即实际成本低于预算，成本控制良好（有结余），CPI&lt;1：实际成本超预算。 CPI = EV / AC 例：CPI为1.05时 项目100元的成本创造了105元的价值</li><li><code>ETC</code>：完工尚需估算，完成剩余工作还需要的成本估算</li><li><code>EAC</code>：完工估算，完成整个项目预计需要的总成本</li></ul><h3 id="计算公式" tabindex="-1">计算公式 <a class="header-anchor" href="#计算公式" aria-label="Permalink to &quot;计算公式&quot;">​</a></h3><ul><li>CV = EV - AC &gt;0 节省</li><li>SV = EV - PV &gt;0 提前</li><li>CPI = EV / AC &gt;1 节省</li><li>SPI = EV / PC &gt;1 提前</li><li>典型：出现偏差后，还照原计划 <ul><li>ETC = (BAC - EV) / CPI</li><li>EAC = AC + ETC = AC + (BAC - EV) / CPI</li></ul></li><li>非典型：出现偏差后，纠错\\改善 <ul><li>ETC = BAC - EV</li><li>EAC = AC + ETC = AC + BAC - EV</li></ul></li></ul><h2 id="总时差、标准时差" tabindex="-1">总时差、标准时差 <a class="header-anchor" href="#总时差、标准时差" aria-label="Permalink to &quot;总时差、标准时差&quot;">​</a></h2><h2 id="六标时" tabindex="-1">六标时 <a class="header-anchor" href="#六标时" aria-label="Permalink to &quot;六标时&quot;">​</a></h2>',8)]))}const h=a(o,[["render",t]]);export{s as __pageData,h as default};
